module.exports = function(RED) {
	function NodeRedTestNodeLibraryNode(config) {
		RED.nodes.createNode(this,config);
		var node = this;
		
		node.on('input', function(msg) {
			msg.payload = msg.payload.toString().toLowerCase();
			node.send(msg);
		});
	}
 	
	RED.nodes.registerType("node-red-test-node-library", NodeRedTestNodeLibraryNode);
}
